import React from "react";
import "./index.css";

class Counter extends React.Component {
  state = {
    counter: 0,
  };

  getModifier() {
    if (this.state.counter > 0) return "counter_title--increment";
    if (this.state.counter < 0) return "counter_title--decrement";
    return "";
  }

  render() {
    const { counter } = this.state;
    return (
      <div className="counter">
        <h1 className={`counter_title ${this.getModifier()}`}>{counter}</h1>
        <section className="buttons">
          <button
            onClick={() => this.setState({ counter: counter + 1 })}
            className="button button--increment"
          >
            incrementar
          </button>
          <button
            onclick={() => this.setState({ counter: counter - 1 })}
            className="button button--increment"
          >
            decrementar
          </button>
        </section>
      </div>
    );
  }
}

export default Counter;
